import React, {useState} from "react";

import styled from 'styled-components'

import { TaskAPI } from "../../api/taskAPI"

import { useDispatch } from "react-redux";

const CreatorArea = styled.div`
  background-color: #8eb0ad;
  min-height: 90vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: calc(10px + 2vmin);
  color: white;
`


function TaskCreator() {

  const [taskList, setTaskList] = useState(
    []
  );



  function submitTasks(e) {
    console.log('here')
    e.preventDefault();
    if (taskList.length > 0) {
      TaskAPI.createMany(taskList)
    } else {
      console.log('You need to add some tasks')
    }
  }

  return (
    <CreatorArea>
      <textarea className="TaskText" rows={10} cols={60}></textarea>
      <button onClick={this.submitTasks}>
        Submit Tasks
      </button>
      </CreatorArea>

  );
}

export default TaskCreator;
