import React, { useState } from "react";

import styled from 'styled-components'

import { TaskAPI } from "../../api/taskAPI"


const CreatorArea = styled.div`
  background-color: #8eb0ad;
  min-height: 90vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

const CreatorTextArea = styled.textarea`
    line-height: 30px;
    height: 30vh;
    width: 30vh;
    padding: 0 10px;
    border: 1;
    border-color: white;
    border-radius: 5px;
    outline: 0;
    color: white;
    font-family: 'Almarai', cursive;
    text-align: left;
    font-weight: bold;
    font-size: 14px;
    background-color: white;
    color: lightgray;
    top: 30px; left: 0; bottom: 30px; right: 60px;
    background: linear-gradient(transparent, transparent 28px, #91D1D3 28px);
    background-size: 30px 30px;
    z-index: 1;
`

function TaskCreator() {

  const [taskList, setTaskList] = useState(
    []
  );



  function submitTasks(e) {
    console.log('here')
    e.preventDefault();
    if (taskList.length > 0) {
      TaskAPI.createMany(taskList)
    } else {
      console.log('You need to add some tasks')
    }
  }

  return (
    <CreatorArea>
      <CreatorTextArea>
      </CreatorTextArea>
      <button onClick={submitTasks}>
          Submit Tasks
        </button>
    </CreatorArea>

  );
}

export default TaskCreator;
