import React, { Component } from "react";
import './App.css'

var todoItems = [];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todoList: todoItems
    };
  }

  renderTasks = () => {

    return todoItems.map((item) => (
      <li>
      </li>
    ));
  };

  submitTasks = () => {
    // Post the tasks here and show the response if it's successful.
    fetch('https://api.npms.io/v2/invalid-url')
        .then(async response => {
            const data = await response.json();

            // check for error response
            if (!response.ok) {
                // get error message from body or default to response statusText
                const error = (data && data.message) || response.statusText;
                return Promise.reject(error);
            }

            this.setState({ totalReactPackages: data.total })
        })
        .catch(error => {
            this.setState({ errorMessage: error.toString() });
            console.error('There was an error!', error);
        });
  };


  render() {

    return (
      <div className='App'>
        <header className="App-header">
          <h1> Speeday </h1>
        </header>

        <div className="Tasks">
          <div className="TaskWriter">
            <textarea className="TaskText" rows={10} cols={60}></textarea>
          </div>
          <div className="TaskList"><div className="mb-4">
            <button onSubmit={this.submitTasks()}>
              Submit Tasks
            </button>
          </div>
            {this.renderTasks()}
          </div>

        </div>
      </div >



    );
  }
}

export default App;
