import React, { Component } from "react";
import './App.css'
import { TaskAPI } from "./api/taskAPI"

var todoItems = {};

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      todoList: todoItems
    };
    this.submitTasks = this.submitTasks.bind(this);

  }


  // renderTasks = () => {

  //   return todoItems.map((item) => (
  //     <li>
  //     </li>
  //   ));
  // };

  submitTasks(e) {
    console.log('here')
    e.preventDefault();
    if (this.state.todoList.length > 0) {
      TaskAPI.createMany(this.state.todoList)
    } else {
      console.log('You need to add some tasks')
    }
  }


  render() {

    return (
      <div className='App'>
        <header className="App-header">
          <h1> Speeday </h1>
        </header>

        <div className="Tasks">
          <div className="TaskWriter">
            <textarea className="TaskText" rows={10} cols={60}></textarea>
          </div>
          <div className="TaskList"><div className="mb-4">
            <button onClick={this.submitTasks}>
              Submit Tasks
            </button>
          </div>
            {/* {this.renderTasks()} */}
          </div>

        </div>
      </div >



    );
  }
}

export default App;
