import React, { Component } from "react";

import { TaskAPI } from "../../api/taskAPI"

import { useDispatch } from "react-redux";

var todoItems = {};

const taskCreator = styled.div`
background-color: #8eb0ad;
min-height: 90vh;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
font-size: calc(10px + 2vmin);
color: white;
`


class TaskCreator extends Component {

  constructor(props) {
    super(props);
    this.state = {

      todoList: todoItems
    };
    this.submitTasks = this.submitTasks.bind(this);



  }



  submitTasks(e) {
    console.log('here')
    e.preventDefault();
    if (this.state.todoList.length > 0) {
      TaskAPI.createMany(this.state.todoList)
    } else {
      console.log('You need to add some tasks')
    }
  }


  render() {

    return (
      <div className="TaskWriter">
        <textarea className="TaskText" rows={10} cols={60}></textarea>
        <button onClick={this.submitTasks}>
          Submit Tasks
        </button>
      </div>



    );
  }
}

export default TaskCreator;
