import React, { useState } from "react";

import styled from 'styled-components'

import { TaskAPI } from "../../api/taskAPI"


const CreatorArea = styled.div`
  background-color: #8eb0ad;
  align-items: center;
  justify-content: center;
  font-size: calc(10px + 2vmin);
  color: white;
  top: 30px; left: 0; bottom: 30px; right: 60px;
  background: linear-gradient(transparent, transparent 28px, #91D1D3 28px);
  background-size: 30px 30px;
`

const CreatorTextArea = styled(CreatorArea)`
    line-height: 30px;
    padding: 0 10px;
    border: 0;
    outline: 0;
    background: transparent;
    color: mediumblue;
    font-family: 'Almarai', cursive;
    text-align: right;
    font-weight: bold;
    font-size: 14px;
`

function TaskCreator() {

  const [taskList, setTaskList] = useState(
    []
  );



  function submitTasks(e) {
    console.log('here')
    e.preventDefault();
    if (taskList.length > 0) {
      TaskAPI.createMany(taskList)
    } else {
      console.log('You need to add some tasks')
    }
  }

  return (
    <CreatorArea>
    <CreatorTextArea>
      <textarea autofocus className="TaskText" rows={10} cols={60}></textarea>
      <button onClick={submitTasks}>
        Submit Tasks
      </button>
    </CreatorTextArea>
    </CreatorArea>

  );
}

export default TaskCreator;
